import React, { useState } from 'react';

class Manufacturer {
    constructor(company_name, location, pan_num, entity, govt_b, gst_num) {
        this.company_name = company_name;
        this.location = location
        this.pan_num = pan_num
        this.entity = entity
        this.govt_b = govt_b
        this.gst_num = gst_num
    }
}

function validateGST(gst_i) {
    // GST validation logic
}

function ManufacturerForm() {
    const [formData, setFormData] = useState({});
    const [allManufacturers, setAllManufacturers] = useState([]);

    function handleSubmit(event) {
        event.preventDefault();

        const { company_name, location, pan_num, entity, govt_b, gst_num } = formData;

        // PAN Validation
        if (!pan_num || pan_num.length !== 10 || pan_num[3] !== "C") {
            alert("Enter valid PAN details");
            return;
        }

        // Entity Validation
        if (!entity || isNaN(entity) || parseInt(entity) <= 0) {
            alert("Enter valid number of entities");
            return;
        }

        // Govt Value Validation
        if (!govt_b || (govt_b !== "Yes" && govt_b !== "No")) {
            alert("Select if your organization belongs to government bodies");
            return;
        }

        // GST Validation
        const generated_gst = location + pan_num + entity + (govt_b === "Yes" ? "Yes" : "No");
        const gstValidationResult = validateGST(generated_gst);
        if (!gstValidationResult) {
            alert("Generated GST is invalid");
            return;
        }

        // GST Input Validation
        if (!gst_num || gst_num !== generated_gst) {
            alert("Enter valid GST Number");
            return;
        }

        // All validations pass, create Manufacturer object and add to allManufacturers
        const manufacturerObj = new Manufacturer(company_name, location, pan_num, entity, govt_b, gst_num);
        setAllManufacturers([...allManufacturers, manufacturerObj]);
        setFormData({}); // Reset form data
        alert("Details are verified and successfully registered");
    }

    function handleInputChange(event) {
        const { name, value } = event.target;
        setFormData({ ...formData, [name]: value });
    }

    return (
        <div id="container">
            <form id="register_details" onSubmit={handleSubmit}>
                <h2 id="sign_up_head">SIGN-UP</h2>
                <label htmlFor="company_name">Company Name</label>
                <input type="text" id="company_name" name="company_name" placeholder="Company Name" onChange={handleInputChange} required />

                {/* Other form inputs go here */}

                <input type="submit" value="Register" />
            </form>
        </div>
    );
}

export default ManufacturerForm;
