import React, { useState } from 'react';

class Manufacturer {
    constructor(company_name, location, pan_num, entity, govt_b, gst_num) {
        this.company_name = company_name;
        this.location = location;
        this.pan_num = pan_num;
        this.entity = entity;
        this.govt_b = govt_b;
        this.gst_num = gst_num;
    }
}

function validateGST(gst_i) {
    // Your existing validation code here
}

function Signup2() {
    const [allManufacturers, setAllManufacturers] = useState([]);
    const [generatedGST, setGeneratedGST] = useState('');
    const [isValid, setIsValid] = useState(true);
    const [errorMessage, setErrorMessage] = useState('');
    const [isSignUp, setIsSignUp] = useState(true);

    const handleSignUpClick = () => {
        setIsSignUp(true);
    };

    const handleSignInClick = () => {
        setIsSignUp(false);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Form submission logic
        const form = event.target;
        const formData = new FormData(form);
        const company_name = formData.get('company_name');
        const location = formData.get('location');
        const pan_num = formData.get('pan_num');
        const entity = formData.get('entity');
        const govt_b = formData.get('govt_b');
        const gst_num = formData.get('gst_num');

        // Validate PAN and GST
        let isValid = true;
        if (pan_num.length !== 10 || pan_num[3] !== 'C') {
            isValid = false;
            setErrorMessage('Invalid PAN details');
        } else if (gst_num[12] !== entity) {
            isValid = false;
            setErrorMessage('Entity problem');
        } else if (govt_b !== gst_num[13]) {
            isValid = false;
            setErrorMessage('Government value problem');
        } else {
            const isValidGST = validateGST(gst_num);
            if (!isValidGST) {
                isValid = false;
                setErrorMessage('Invalid GST number');
            }
        }

        if (isValid) {
            const manufacturer = new Manufacturer(company_name, location, pan_num, entity, govt_b, gst_num);
            setAllManufacturers([...allManufacturers, manufacturer]);
            setGeneratedGST(gst_num);
            setErrorMessage('');
            alert('Details are verified and successfully registered');
            form.reset();
        }
    };

    const handleGetData = () => {
        fetch("https://app.signalx.ai/apps/gst-verification/gstin-overview/29AACCF0683K1ZD", ["GET"])
            .then(data => {
                console.log('Data Received', data);
            })
            .catch(error => {
                console.error("Problem in receiving the data");
            });
    };

    return (
        <div>
            {isSignUp ? (
                <form id="register_details" onSubmit={handleSubmit}>
                    {/* Input fields for company details */}
                    <input type="text" name="company_name" placeholder="Company Name" required />
                    <input type="text" name="location" placeholder="Location" required />
                    <input type="text" name="pan_num" placeholder="PAN Number" required />
                    <input type="text" name="entity" placeholder="Entity" required />
                    <input type="text" name="govt_b" placeholder="Government Value" required />
                    <input type="text" name="gst_num" placeholder="GST Number" required />
                    <button type="submit">Register</button>
                </form>
            ) : (
                <div>
                    {/* Sign-in form */}
                </div>
            )}
            {/* Buttons to toggle between sign-up and sign-in */}
            <button onClick={handleSignUpClick}>Sign Up</button>
            <button onClick={handleSignInClick}>Sign In</button>
            {/* Error message display */}
            {errorMessage && <p>{errorMessage}</p>}
        </div>
    );
}

export default Signup2;
